
$(document).ready(function () {
    var e = new LiveValidation("name");
    e.add(Validate.Presence);
    var t = new LiveValidation("email");
    t.add(Validate.Email);
    t.add(Validate.Presence);
   if (screen.width >= 1024 && screen.height >= 768) { 
	    tinyMCE.init({
	        mode: "textareas",
	        theme: "simple",
	        width: "300px",
	        height: "150px",
	        setup: function (e) {
	            e.onInit.add(function (e, t) {
	                tinymce.dom.Event.add(e.getBody(), "blur", function (e, t) {
	                    var n = tinyMCE.activeEditor.getContent();
	                    if (n == "") $("#notThere").show();
	                    else $("#notThere").hide()
	                })
	            });
	            e.onPostProcess.add(function (e, t) {
	                var n = "";
	                var r = document.createElement("DIV");
	                r.innerHTML = t.content;
	                if (r.innerHTML) {
	                    n = r.textContent || r.innerText || "";
	                    n = n.replace(/\n/gi, "");
	                    n = n.replace(/\s/g, "");
	                    n = n.replace(/\t/g, "")
	                } else {
	                    n = ""
	                }
	                if (n == "") {
	                    t.content = n
	                }
	            })
	        }
	    });
    }else{
    	tinyMCE.init({
    	    mode: "textareas",
    	    theme: "advanced",
    	    width: "800px",
	    height: "400px",
	    theme_advanced_buttons1: "",
	    theme_advanced_buttons2: "",
	    theme_advanced_buttons3: "",
	    theme_advanced_path: false,
    	    setup: function (e) {
    	        e.onInit.add(function (e, t) {
    	            tinymce.dom.Event.add(e.getBody(), "blur", function (e, t) {
    	                var n = tinyMCE.activeEditor.getContent();
    	                if (n == "") $("#notThere").show();
    	                else $("#notThere").hide()
    	            })
    	        });
    	        e.onPostProcess.add(function (e, t) {
    	            var n = "";
    	            var r = document.createElement("DIV");
    	            r.innerHTML = t.content;
    	            if (r.innerHTML) {
    	                n = r.textContent || r.innerText || "";
    	                n = n.replace(/\n/gi, "");
    	                n = n.replace(/\s/g, "");
    	                n = n.replace(/\t/g, "")
    	            } else {
    	                n = ""
    	            }
    	            if (n == "") {
    	                t.content = n
    	            }
    	        })
    	    }
    	});
    }
    $(".submit").click(function () {
        var e = tinyMCE.activeEditor.getContent();
        if (e == "") {
            $("#notThere").show();
            return false
        }
    })
})